import useStats from '../utils/useStats';
import Stats from './Stats';
import { useState } from 'react';
export default function CountrySelector() {
  const countries = useStats('https://covid19.mathdro.id/api/countries');
  const [selectedCountry, setSelectedCounty] = useState('HU');
  if (!countries) {
    return <p>..Loading</p>;
  }
  return (
    <div>
      <select
        onChange={e => {
          setSelectedCounty(e.target.value);
        }}
      >
        {Object.entries(countries.countries).map(([country, code], index) => {
          console.log(code.name);
          return (
            <option key={index} value={code}>
              {code.name}
            </option>
          );
        })}
      </select>
      <Stats url="https://covid19.mathdro.id/api/countries/ES"></Stats>
    </div>
  );
}
